package javaprogram;

import java.util.Arrays;

public class Matrixs {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	for (int i = 1; i < 5; i++) {
		for (int j = 1; j < 5; j++) {
			if(i==1 ||i==4 ||j==1||j==4)
			{
				System.out.print("1 ");
			}
			else
			{
				System.out.print("  ");
			}
		
			
		}
		System.out.println();
		
	}
	
	}

}
